problem.script:
def hint_fn(answer_ids, student_answers, new_cmap, old_cmap):
  aid = answer_ids[0]
  ans = ''
  feedback = ''
  correctness = False
  if not student_answers:
  	feedback = 'Au moins une des possibilités proposées est solution du système.'
  else:
    ans = str(student_answers[aid]).lower()
  print 'hint_fn called, ans=', ans
  if 'choice_0' in ans and 'choice_1' not in ans:
    feedback = 'En effet, \( \\alpha=(1,-1)\) satisfait à la première équation du système. Cependant, est-ce également le cas de la deuxième?'
  elif 'choice_1' in ans and 'choice_0' not in ans:
    feedback = 'En effet, \( \\alpha=(-1,3)\) satisfait à la première équation du système. Cependant, est-ce également le cas de la deuxième?'
  elif 'choice_0' in ans and 'choice_1' in ans:
    feedback = 'En effet, chacune des possibilités cochées satisfait à la première équation du système. Cependant, est-ce également le cas de la deuxième?' 
  elif 'choice_0' not in ans and 'choice_1' not in ans and 'choice_2' in ans:
    feedback = 'Correct! En remplaçant \(x\) par \(2\) et \(y\) par \(-3,\) on remarque que les deux équations du système sont bel est bien vérifiées. Ce n\'est pas le cas de  \(\\alpha=(1,-1)\)  et \(\\alpha=(-1,3),\) qui même si elles satisfont à la première équation, ne satisfont pas à la deuxième.'
    correctness = True
  if feedback:
    if correctness is True:
      feedback = "<font color='green'>{0}</font>".format(feedback)
    else:
      feedback = "<font color='red'>{0}</font>".format(feedback)
    new_cmap.set_hint_and_mode(aid,feedback,'always')
